Integer number = new Integer(0);
ctmWorkspace.getAllFolders().each{ ctmFolder ->
	CtmXmlElement folderElement = ctmFolder.getFolderInputXmlData();
	def queue =  folderElement.getFirstChildElementByName("DefaultQueue").getAttribute("type");
	if ((queue != null) && queue.equals("Queue"))
	{
	    if (ctmFolder.getChildren().size() == 0){
            def listElements = folderElement.getAllDecnedElementByName("JobChainStep");
            //run over each "JobChainStep" element and look for the "JobChainCall" that located under it
    	    listElements.each { listElement ->
    		number = workOnStepElement(ctmFolder, number, listElement);
    	 }	        
	    }
    }

}
def workOnStepElement(CtmFolder folderx, Integer number, CtmXmlElement stepElement) 
	{
		def callElements = stepElement.getAllDecnedElementByName("JobChainCall");
		//run over each "JobChainCall" element
		callElements.each { callElement ->
			number = workOnCallElement(folderx, number, callElement);			
		}
		return number;
	}
def workOnCallElement(def folderx, Integer number, CtmXmlElement jobChainCall)
				{
				    //catch the "JobDefinition" element and extract the name of the job/sub folder that should be placed inside the folder
					CtmXmlElement jobDefintionElement = jobChainCall.getFirstChildElementByName("JobDefinition");
					String jobPath = jobDefintionElement.getAttribute("path");
					String regexPattern = "(.*)\\.(.*)";
					Pattern pattern = Pattern.compile(regexPattern);
					Matcher matcher = pattern.matcher(jobPath);
					if(matcher.find())
					{
						String jobName = matcher.group(2);
						def entity = ctmWorkspace.getJobsByName(jobName)[0];
						if (entity == null){
						        entity = ctmWorkspace.getFoldersByName(jobName)[0];
						        if (entity !=null){
						                entity = getEntityWithoutParent(entity);
    						            number = workOnFolderElement(folderx, number, jobChainCall, entity);
						             }
						        else{
						        }
						} else {
						    entity = getEntityWithoutParent(entity);
							number = workOnJobElement(folderx, number, jobChainCall, entity);
						}
					} 

					return number;
				}
				//placed the job inside the folder "folderx"
				def workOnJobElement(def folderx, Integer number, CtmXmlElement callForJobElement, CtmJob ctmJob)
				{
					number = addEntityToFolder(ctmJob, folderx, number);		
					return number;
				}
				//placed the sub folder inside the folder "folderx" and then run over all the sub folder childrens (jobs or more sub folders)
				def workOnFolderElement(def folderx, Integer number, CtmXmlElement callForJobChain, CtmFolder ctmFolder)
				{
					addEntityToFolder(ctmFolder, folderx,number)
					if (ctmFolder.getChildren().size() == 0){
        					def listElements = ctmFolder.getFolderInputXmlData().getAllDecnedElementByName("JobChainStep");
        					listElements.each { listElement ->
        						number = workOnStepElement(ctmFolder, number, listElement);
        					}
					}
					return number;
				}
				//placed the entity(job/sub folder) inside the folder "folderx"
				def addEntityToFolder(def entity, def folderx, Integer number)
				{
					if(entity != null && folderx != null)
					{

						entity.setDescription(number.toString());
						number = number + 1;
						if (entity instanceof CtmFolder){
						      ctmWorkspace.insertFolderToFolder(entity, folderx);
						}
						else{
						      ctmWorkspace.insertJobToFolder(entity, folderx);
						}
					}
					return number;
				}

			//create new entity from the entity definition
			def getEntityWithoutParent(def entity){
		     	if (entity.getParentFolder() == null){
			            return entity;
			    }
                else{
                    def newEntity;
                    if (entity instanceof CtmFolder){
                        newEntity = ctmWorkspace.createFolder(entity.getFolderInputXmlData())
                        newEntity.general().setName(entity.getName());
                    }
                    else{
                         newEntity = ctmWorkspace.createJob(entity.getJobInputXmlData())
                         newEntity.general().setName(entity.getName());
                    }
                    return newEntity; 
                }			            
			}

