//generate unique condition name (for example: if there is already condition name "x" then the method return "x_1")
def getUniqueConditionName(String originalConditionName){
    String toRet = originalConditionName;
    if(conditionNameMap.containsKey(originalConditionName)){
        int suffix = conditionNameMap[originalConditionName];
        conditionNameMap[originalConditionName] = suffix + 1;
        toRet = toRet + "_" + suffix;
    }
    else{
        conditionNameMap[originalConditionName] = 1;
    }
    return toRet;
}

//  ---- Create SapR3 Job Rule ----
ctmWorkspace.getAllJobs().each{ ctmJob ->
        CtmXmlElement element = ctmJob.getJobInputXmlData();
        String type = element.getDirectChildsElementAttrByName("JobDefinitionType", "path")[0];
        if (type.contains(".SAPR3")){
               String sapType = getSapJobType(element);
               //convert job to SAPR3 with Action: "Create a new job"
               if(sapType.equals("ABAP_RUN_PRINT")){
                  setSapR3Attributes(ctmJob);    
               }
               //convert job to SAPR3 with Action: "Copy an existing job"
               if(sapType.equals("TEMPLATE_JOB_RUN")){
                   setSapCopyExsitingJob(ctmJob);
               }
        }
}

//convert job to SAPR3 with Action: "Copy an existing job"
def setSapCopyExsitingJob(CtmJob ctmJob){
    def jobElementData = ctmJob.getJobInputXmlData();
    ctmJob.setSapR3Type();
    ctmJob.general().sapR3Parameters().setAction(SapR3Action.CopyAnExistingJob);
    List<CtmXmlElement> parametersList = jobElementData.getDirectChildsElementbyName("JobDefinitionParameter");
    parametersList.each{ parameterElement -> 
      def parameterNameElement = parameterElement.getFirstChildElementByName("Name");
      String parameterName = parameterNameElement.getText();
      def value = parameterElement.getValueOfFirstChildElementByName("DefaultExpression");
      if(parameterName.equals("JOBNAME")){
          ctmJob.general().sapR3Parameters().setSAPJobName(value);
      }
    }
    ctmJob.general().sapR3Parameters().setConnectionProfile("DEFAULT_SAP_CONNECTION_PROFILE");
}
//extract the SAPR3 job type ("ABAP_RUN_PRINT","TEMPLATE_JOB_RUN" ...)
def getSapJobType(CtmXmlElement jobElement){
    CtmXmlElement sapScriptElement = jobElement.getFirstChildElementByName("SAPScript");
    CtmXmlElement sapScriptAttr = sapScriptElement.getFirstChildElementByName("SAPScriptAttribute");
    String jobType = sapScriptAttr.getValueOfFirstChildElementByName("Value");
    return jobType;
}

//convert job to SAPR3 with Action: "Create a new job"
def setSapR3Attributes(CtmJob ctmJob){
    ctmJob.setSapR3Type();
    ctmJob.general().sapR3Parameters().setAction(SapR3Action.CreateANewJob);
	CtmXmlElement jobElementData = ctmJob.getJobInputXmlData();
        //set job's description
        setJobDescription(ctmJob);
        //set job's connectionProfile
    	String jobDefaultQueuePathValue = jobElementData.getFirstChildElementByName("DefaultQueue").getAttribute("path");
		String regexPattern = ".*\\.(.*)";
		Pattern pattern = Pattern.compile(regexPattern);
		Matcher matcher = pattern.matcher(jobDefaultQueuePathValue);
		if(matcher.find() && matcher.group(1))
		{
			String connectionProfile = matcher.group(1);
				ctmJob.general().sapR3Parameters().setConnectionProfile(connectionProfile);
					
		}
		else{
		    ctmJob.general().sapR3Parameters().setConnectionProfile("DEFAULT_SAP_CONNECTION_PROFILE");
		}
        
        //set job's abap step + variables
        List<CtmXmlElement> parametersList = jobElementData.getDirectChildsElementbyName("JobDefinitionParameter");
        SapR3AbapStepAPI step = ctmJob.general().sapR3Parameters().addAbapStep();
        parametersList.each{ parameterElement -> 
        def parameterNameElement = parameterElement.getFirstChildElementByName("Name");
        String parameterName = parameterNameElement.getText();
        def value = parameterElement.getValueOfFirstChildElementByName("DefaultExpression");
        if(value.equals(""))return;
        switch (parameterName) {
            case "JOBNAME" :
                ctmJob.general().sapR3Parameters().setSAPJobName(value);
                break;
            case "JOBCLASS" :
                setSapJobClass(ctmJob,value);
                break; 
                case "ABAP_PROGRAM_NAME" :
                step.setAbapProgramName(value);
                break;
            case "ABAP_VARIANT_NAME" :
                step.setVariantName(value);
                break;
            case "TEMPORARY_VARIANT" :
                value = convertYesNoToBoolean(value);
                if(value){
                step.setTemporaryVariant();
                }
                break;
            case "SAP_USER_NAME" :
                step.setUser(value);
                break;
            case "LANGUAGE" :
                step.setLanguage(value);
                break;
            case "PRINT_ARMOD":
                value = getPrintArchiveMode(value);
                step.setPrintArchiveMode(value);
                break;
            case "PRINT_PDEST" :
                step.setOutputDevice(value);
                break;
            case "PRINT_PRTXT" :
                step.setDepartment(value);
                step.setRecipient(value);
                break;
            case "PRINT_PRCOP" :
                step.setNumberOfCopies(value);
                break;
            case "PRINT_PRNEW" :
                value = convertYesNoToBoolean(value);
                if(value){
                    step.enableNewSpoolRequest();
                }
                else{
                    step.disableNewSpoolRequest();
                }
                break;
            case "PRINT_PLIST" :
                step.setSpoolRequestName(value);
                break;
            case "PRINT_PRIMM" :
                value = convertYesNoToBoolean(value);
                if(value){
                    step.setTimeOfPrint(TimeOfPrint.PrintOutImmediately);
                }
                else{
                    step.setTimeOfPrint(TimeOfPrint.PrintLater);
                }
                break;
            case "PRINT_PRREL" :
                value = convertYesNoToBoolean(value);
                if(value){
                    step.enableDeleteAfterPrint();
                }
                else{
                    step.disableDeleteAfterPrint();
                }
                break;
            case "PRINT_PEXPI" :
                step.setPrintExpiration(value);
                break;
            case "PRINT_LICT" :
                step.setOutputFormatRows(value);
                break;
            case "PRINT_LISZ" :
                step.setOutputFormatColumns(value);
                break;
            case "PRINT_PAART" :
                step.setOutputFormatLayout(value);
                break;
            case "PRINT_PRBIG" :
                value = convertYesNoToBoolean(value);
                if(value){
                    step.enableSelectionCoverPage();
                }
                else{
                    step.disableSelectionCoverPage();
                }
                break;
            case "PRINT_PRSAP" :
                value = getSapCoverPage(value);
                step.setSapCoverPage(value);
                break;
            case "PRINT_PRBER" :
                step.setSpoolRequestAuthorization(value);
                break;
            case "PRINT_PRUNX" :
                value = getOsCoverSheet(value);
                step.setOsCoverSheet(value);
                break;
            case "ARCHIVE_SAP_OBJECT" :
                step.setArchiveId(value);
                break;
            case "ARCHIVE_AR_OBJECT" :
                step.setArchiveDocumentType(value);
                break;
            case "ARCHIVE_INFO" :
                step.setArchiveInformationField(value);
                break;
            case "ARCHIVE_ARCTEXT" :
                step.setArchiveText(value);
                break;
            default:
            setJobVariable(ctmJob,parameterElement,parameterName);
            break;
                 
        }
            
    }

}

def convertYesNoToBoolean(String value){
    if(value.equalsIgnoreCase("Y")){
     return true;
    }
    else{
     return false;
    }
}

def getSapCoverPage(String value){
    if(value.equalsIgnoreCase("D")){
        return SapCoverPage.DefaultSetting;
    }
    else{
        value = convertYesNoToBoolean(value);
        if(value){
            return SapCoverPage.Print;
        }
        else{
            return SapCoverPage.DoNotPrint;
        }
    }
}

def getOsCoverSheet(String value){
    if(value.equalsIgnoreCase("D")){
        return OsCoverSheet.DefaultSetting;
    }
    else{
        value = convertYesNoToBoolean(value);
        if(value){
            return OsCoverSheet.Print;
        }
        else{
            return OsCoverSheet.DoNotPrint;
        }
    }
}

def getPrintArchiveMode(String value){
    switch(value){
        case "1" :
            return PrintArchiveMode.Print;
            break;
        case "2" :
            return PrintArchiveMode.Archive;
            break;
        case "3" :
            return PrintArchiveMode.PrintAndArchive
            break;
        default :
            return "";
    }
}

def setSapJobClass (CtmJob ctmJob,String value){
     switch (value){
        case "A" :     
                ctmJob.general().sapR3Parameters().setJobClass(SapR3JobClass.HighPriority);
                break; 
        case "B" :     
                ctmJob.general().sapR3Parameters().setJobClass(SapR3JobClass.MediumPriority);
                break; 
        case "C" :     
                ctmJob.general().sapR3Parameters().setJobClass(SapR3JobClass.LowPriority);
                break; 
     }
}

def setJobDescription(CtmJob ctmJob){
    def jobDef = ctmJob.getJobInputXmlData();
	String description = jobDef.getValueOfFirstChildElementByName("Description");
	ctmJob.setDescription(description);
}

def setJobVariable(CtmJob ctmJob, CtmXmlElement parameterElement, String parameterName){
		if(shouldIgonreParameterWithName(parameterName))
			return;
		variableName = parameterName.startsWith("%%") ? parameterName.replace("%%","") : parameterName;
		String variableValue = parameterElement.getValueOfFirstChildElementByName("DefaultExpression");
		ctmJob.general().addLocalVariable(variableName, variableValue);
}


def shouldIgonreParameterWithName(String parameterName) {
	return ["JOBNAME","ABAP_PROGRAM_NAME","ABAP_VARIANT_NAME","TEMPORARY_VARIANT","JOBCLASS","SAP_USER_NAME","LANGUAGE","TARGET_SERVER",
			"TARGET_GROUP","PRINT_ARMOD","PRINT_PDEST","PRINT_PRTXT","PRINT_PRCOP","PRINT_PRNEW","PRINT_PLIST","PRINT_PRIMM","PRINT_PRREL",
			"PRINT_PEXPI","PRINT_LICT","PRINT_LISZ","PRINT_PAART","PRINT_PRBIG","PRINT_PRSAP","PRINT_PRREC","PRINT_PRABT","PRINT_PRBER",
			"PRINT_PRDSN","PRINT_PTYPE","PRINT_FOOTL","PRINT_PRUNX","DRAFT_MODE","PAGE_ORIENTATION","ARCHIVE_SAP_OBJECT","ARCHIVE_AR_OBJECT",
			"ARCHIVE_INFO","ARCHIVE_ARCTEXT","SHOWLOG","SHOWSPOOL","DELETE_JOB","JOBCOUNT","SAP_SYSTEMS","CLIENT"].contains(parameterName);			
}

//  ---- Create Os Job Rule ----

ctmWorkspace.getAllJobs().each{ ctmJob ->

        CtmXmlElement element = ctmJob.getJobInputXmlData();
        String type = element.getDirectChildsElementAttrByName("JobDefinitionType", "path")[0];
        //if the job type is different then SAPR3 and the job has script element then he will be converted to Embedded Script job
        if (!type.contains(".SAPR3") && isContainsScript(element)){
            ctmJob.setOsType();
            setJobDescription(ctmJob, type);
        }
}
//check if the job contains Script element
def isContainsScript(element){
    def scriptElement = element.getDirectChildsElementbyName("Script");
    return scriptElement.size() > 0;
}
