def folderList = ctmWorkspace.getAllFolders();
conditionNameMap = [:];
folderList.each { ctmFolder -> 
    def childrenList = ctmFolder.getChildren();
    if(childrenList.size() > 1){
        //set the condition between the entites by the "JobChainCall" order
        childrenList.sort{c1, c2 -> c1.getDescription().compareTo(c2.getDescription())};
        for(int i = 0 ; i < childrenList.size()-1 ; i++)
        {
            def sourceEntity = childrenList.get(i);
            def targetEntity = childrenList.get(i+1);
            def conditionName = sourceEntity.getName()+ "-TO-" + targetEntity.getName();
            conditionName = getUniqueConditionName(conditionName);
            sourceEntity.actions().addOutCondition(conditionName);
            targetEntity.prerequisites().addInCondition(conditionName);
        }
    }
    
    //Set folder's Description
    CtmXmlElement folderDef = ctmFolder.getFolderInputXmlData();
	String description = folderDef.getValueOfFirstChildElementByName("Description");
	ctmFolder.setDescription(description);
}

