def setApplicationAndSubApplication(CtmJob ctmJob){
    CtmXmlElement xmlElement = ctmJob.getJobInputXmlData();
	String jobDefPathValue = xmlElement.getFirstChildElementByName("ParentApplication").getAttribute("path");
	String regexPattern = "(.*)\\.(.*)\\.(.*)";
	Pattern pattern = Pattern.compile(regexPattern);
	Matcher matcher = pattern.matcher(jobDefPathValue);
	if(matcher.find())
	{
	    if(matcher.group(2))
	    {
           String application = matcher.group(2);
	       ctmJob.setApplication(application);
	    }
	    if(matcher.group(3))
	    {
	        String subApplication = matcher.group(3);
	        ctmJob.setSubApplication(subApplication);
	    }
	}
}
